import{_ as l,E as a,ag as t,D as i}from"./chunks/framework.lDRhkpKe.js";const d=JSON.parse('{"title":"贡献者","description":"","frontmatter":{"layout":"home","footer":true,"title":"贡献者"},"headers":[],"relativePath":"guides/committer.md","filePath":"guides/committer.md"}'),e={name:"guides/committer.md"};function o(n,r,s,g,u,h){return i(),a("div",null,r[0]||(r[0]=[t('<h1 id="dromara-开源社区-贡献者" tabindex="-1">Dromara 开源社区 :: 贡献者 <a class="header-anchor" href="#dromara-开源社区-贡献者" aria-label="Permalink to &quot;Dromara 开源社区 :: 贡献者&quot;">​</a></h1><h2 id="邮件列表" tabindex="-1">邮件列表 <a class="header-anchor" href="#邮件列表" aria-label="Permalink to &quot;邮件列表&quot;">​</a></h2><ul><li><strong>重要性：</strong> 在 Dromara 社区中，一切事务需以异步方式进行，并存档于邮件列表中。常言道：“如果没在邮件列表中出现，那它就没发生过。”</li><li><strong>原则：</strong> 所有决策需在公共的邮件列表中进行，而非在 IRC、即时消息、Slack、私人邮件等非公开渠道。</li><li><strong>项目相关邮件列表：</strong><ul><li><strong>开发讨论列表（dev@project）：</strong> 项目开发的主要讨论场所。</li><li><strong>用户列表（user@project 或 users@project）：</strong> 供用户提问和讨论。</li><li><strong>问题列表（issues@project）：</strong> 跟踪项目问题。</li><li><strong>提交列表（commits@project）：</strong> 记录代码提交。</li></ul></li><li><strong>社区相关邮件列表：</strong><ul><li><strong>community：</strong> 供所有社区贡献者讨论 Apache 相关事项。</li><li><strong>infrastructure：</strong> 基础设施团队讨论和处理社区基础设施需求。</li><li><strong>legal-discuss：</strong> 法律相关问题讨论。</li><li><strong>builds：</strong> 持续集成相关的讨论。</li></ul></li><li><strong>行动项：</strong><ul><li>熟悉开发者的邮件交流技巧。</li><li>订阅项目的用户、开发、问题及提交邮件列表。</li><li>关注孵化器的动态，订阅孵化器的通用列表。</li><li>订阅 Apache 社区的基础设施和法律讨论列表。</li></ul></li></ul><h3 id="私密邮件列表" tabindex="-1">私密邮件列表 <a class="header-anchor" href="#私密邮件列表" aria-label="Permalink to &quot;私密邮件列表&quot;">​</a></h3><ul><li><strong>使用场景：</strong><ul><li>项目通常有一个私密邮件列表（如 <a href="mailto:private@project.apache.org" target="_blank" rel="noreferrer">private@project.apache.org</a>），用于处理敏感讨论。</li><li><strong>注意事项：</strong><ul><li>避免在同一邮件中混用公共和私密邮件列表。</li><li>一般仅需发至开发列表，因为 PMC 成员也会订阅该列表。</li></ul></li></ul></li></ul><h2 id="项目网站" tabindex="-1">项目网站 <a class="header-anchor" href="#项目网站" aria-label="Permalink to &quot;项目网站&quot;">​</a></h2><ul><li><strong>资源：</strong> 参考 <a href="http://www.apache.org/dev/" target="_blank" rel="noreferrer">孵化项目网站文档</a>。</li><li><strong>注意事项：</strong><ul><li>学习如何搭建和维护项目网站。</li></ul></li></ul><h2 id="贡献者资源" tabindex="-1">贡献者资源 <a class="header-anchor" href="#贡献者资源" aria-label="Permalink to &quot;贡献者资源&quot;">​</a></h2><ul><li><strong>指南：</strong> 查看 <a href="http://www.apache.org/dev/" target="_blank" rel="noreferrer">新贡献者指南</a> 以获取帮助。</li><li><strong>行动项：</strong><ul><li>经常浏览 Apache 官方开发页面，了解最新资讯。</li><li>订阅基础设施列表，关注幕后支持工作。</li><li>主动响应项目基础设施相关问题，例如网站或代码仓库服务是否正常。</li></ul></li></ul><h2 id="podling-pmc-ppmc" tabindex="-1">Podling PMC (PPMC) <a class="header-anchor" href="#podling-pmc-ppmc" aria-label="Permalink to &quot;Podling PMC (PPMC)&quot;">​</a></h2><ul><li><strong>职责：</strong><ul><li>熟悉 Dromara 社区的孵化政策。</li><li>参阅 <a href="http://www.apache.org/dev/" target="_blank" rel="noreferrer">PPMC 指南</a>。</li></ul></li></ul><h2 id="为新贡献者提供引导" tabindex="-1">为新贡献者提供引导 <a class="header-anchor" href="#为新贡献者提供引导" aria-label="Permalink to &quot;为新贡献者提供引导&quot;">​</a></h2><ul><li><strong>目标：</strong> 通过导师或社区成员引导新贡献者了解社区文化。</li><li><strong>原则：</strong><ul><li>尽量在公开的论坛或邮件列表中进行讨论，除非涉及敏感问题。</li><li>私密讨论仅限安全或个人问题。</li></ul></li><li><strong>导师职责：</strong><ul><li>帮助贡献者逐步适应公开讨论的习惯。</li><li>避免私密邮件列表的过度使用。</li><li>鼓励贡献者优先在项目公共列表中提问。</li></ul></li></ul><h2 id="提问与支持" tabindex="-1">提问与支持 <a class="header-anchor" href="#提问与支持" aria-label="Permalink to &quot;提问与支持&quot;">​</a></h2><ul><li><strong>问题渠道：</strong> 在孵化器的通用邮件列表中提问。</li><li><strong>参考资源：</strong> 查看 <a href="http://www.apache.org/dev/" target="_blank" rel="noreferrer">开发者信息</a> 获取更多帮助。</li></ul><p>以上内容为 Dromara 开源社区贡献者的职责及相关资源指引，为新加入的成员提供清晰的参与路径。</p>',16)]))}const p=l(e,[["render",o]]);export{d as __pageData,p as default};
