import{_ as a,G as i,ag as t,E as e}from"./chunks/framework.DVJavDIB.js";const d=JSON.parse('{"title":"项目管理委员会","description":"","frontmatter":{"layout":"home","footer":true,"title":"项目管理委员会"},"headers":[],"relativePath":"guides/ppmc.md","filePath":"guides/ppmc.md"}'),r={name:"guides/ppmc.md"};function o(n,l,s,p,u,c){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="项目管理委员会-ppmc-指南" tabindex="-1">项目管理委员会（PPMC）指南 <a class="header-anchor" href="#项目管理委员会-ppmc-指南" aria-label="Permalink to &quot;项目管理委员会（PPMC）指南&quot;">​</a></h1><h2 id="ppmc-角色与职责" tabindex="-1">PPMC 角色与职责 <a class="header-anchor" href="#ppmc-角色与职责" aria-label="Permalink to &quot;PPMC 角色与职责&quot;">​</a></h2><p>PPMC 的主要职责是帮助孵化项目学会自我治理。让项目更好的发展，为社区贡献者提供指导。</p><h3 id="ppmc-的构成" tabindex="-1">PPMC 的构成 <a class="header-anchor" href="#ppmc-的构成" aria-label="Permalink to &quot;PPMC 的构成&quot;">​</a></h3><ul><li><strong>初始成员：</strong> 由项目导师和初始贡献者组成。</li><li><strong>职责：</strong><ul><li>监督孵化项目。</li><li>项目发布、和升级。</li><li>提名新的 PPMC，Committer 成员</li></ul></li></ul><h2 id="项目状态报告" tabindex="-1">项目状态报告 <a class="header-anchor" href="#项目状态报告" aria-label="Permalink to &quot;项目状态报告&quot;">​</a></h2><ul><li><strong>提交频率：</strong><ul><li>初创项目每月报告一次。</li></ul></li><li><strong>报告内容：</strong><ul><li>孵化过程中的障碍。</li><li>上次报告以来的进展。</li><li>下阶段计划。</li></ul></li><li><strong>撰写指南：</strong><ul><li>在开发邮件列表中讨论并协作完成报告。</li><li>使用最新模板，避免更改主题格式。</li></ul></li><li><strong>导师签署：</strong><ul><li>导师必须查看、审核和签署报告，并且提交给 TOC 委员会。</li></ul></li></ul><p>以上指南旨在帮助 Dromara 开源社区的 PPMC 成员有效地管理项目，并为社区的长期健康发展奠定基础。</p>',8)]))}const P=a(r,[["render",o]]);export{d as __pageData,P as default};
