import{_ as i,E as r,ag as a,D as o}from"./chunks/framework.lDRhkpKe.js";const d=JSON.parse('{"title":"角色与职责","description":"","frontmatter":{"layout":"home","footer":true,"title":"角色与职责"},"headers":[],"relativePath":"guides/roles_and_responsibilities.md","filePath":"guides/roles_and_responsibilities.md"}'),t={name:"guides/roles_and_responsibilities.md"};function n(e,l,s,u,g,h){return o(),r("div",null,l[0]||(l[0]=[a('<h1 id="dromara-开源社区-角色与职责" tabindex="-1">Dromara 开源社区 :: 角色与职责 <a class="header-anchor" href="#dromara-开源社区-角色与职责" aria-label="Permalink to &quot;Dromara 开源社区 :: 角色与职责&quot;">​</a></h1><h2 id="理事会" tabindex="-1">理事会 <a class="header-anchor" href="#理事会" aria-label="Permalink to &quot;理事会&quot;">​</a></h2><ul><li><strong>角色：</strong> 负责管理 Dromara 社区的组织事务。</li><li><strong>职责：</strong><ul><li>理事由社区成员选举产生。</li><li>理事会将孵化事务的管理责任委托给孵化项目管理委员会（Incubator PMC）。</li></ul></li><li><strong>与孵化的关系：</strong><br> 理事会成员可能会积极参与孵化工作，但其发言通常代表个人观点，非理事会政策，除非另行说明。</li></ul><h2 id="孵化项目管理委员会-pmc" tabindex="-1">孵化项目管理委员会（PMC） <a class="header-anchor" href="#孵化项目管理委员会-pmc" aria-label="Permalink to &quot;孵化项目管理委员会（PMC）&quot;">​</a></h2><ul><li><strong>角色：</strong> 监督和支持项目在孵化过程中发展。</li><li><strong>职责：</strong><ul><li>审核并接受候选项目进入孵化流程。</li><li>指导孵化项目遵循 Dromara 社区的开发哲学和协作原则。</li><li>维护孵化历史记录和信息存储。</li><li>协助导师履行职责。</li><li>定期评估孵化项目状态并建议其： <ul><li>毕业成为正式项目；</li><li>继续接受孵化支持；</li><li>或者终止孵化。</li></ul></li></ul></li><li><strong>其他：</strong><ul><li>孵化项目需定期向 PMC 报告，以帮助 PMC 了解项目状态。</li><li>PMC 成员具备孵化过程的专业知识，可在孵化过程中为项目或导师提供支持。</li><li>成员可通过投票加入 PMC，Dromara 社区成员可以申请加入。</li></ul></li></ul><h2 id="孵化-pmc-主席" tabindex="-1">孵化 PMC 主席 <a class="header-anchor" href="#孵化-pmc-主席" aria-label="Permalink to &quot;孵化 PMC 主席&quot;">​</a></h2><ul><li><strong>角色：</strong> 理事会任命的人员，主要负责孵化 PMC 的政策监督和执行。</li></ul><h2 id="候选项目" tabindex="-1">候选项目 <a class="header-anchor" href="#候选项目" aria-label="Permalink to &quot;候选项目&quot;">​</a></h2><ul><li><strong>定义：</strong> 候选项目是申请孵化的项目。</li><li><strong>要求：</strong><ul><li>项目需有一名“引荐人”（Champion）。</li><li>候选项目可以： <ul><li>指定现有 Dromara 项目为赞助方；</li><li>明确孵化期间的特殊需求；</li><li>总结与现有或计划项目的关系。</li></ul></li></ul></li><li><strong>孵化流程：</strong><ul><li>引荐人提议候选项目进入孵化。</li><li>赞助方批准后，PMC 将项目列为孵化项目并分配导师。</li></ul></li></ul><h2 id="引荐人-champion" tabindex="-1">引荐人（Champion） <a class="header-anchor" href="#引荐人-champion" aria-label="Permalink to &quot;引荐人（Champion）&quot;">​</a></h2><ul><li><strong>角色：</strong> Dromara 社区成员或负责人，支持候选项目申请孵化。</li><li><strong>职责：</strong><ul><li>在孵化前： <ul><li>帮助项目克服流程相关障碍；</li><li>指出可能影响毕业或孵化的潜在问题；</li><li>寻找适合的导师并引导项目进入孵化。</li></ul></li><li>在孵化后，引荐人角色可结束，也可继续担任导师。</li></ul></li></ul><h2 id="赞助方" tabindex="-1">赞助方 <a class="header-anchor" href="#赞助方" aria-label="Permalink to &quot;赞助方&quot;">​</a></h2><ul><li><strong>角色：</strong> 判断候选项目是否符合 Dromara 社区目标，并支持其完成孵化。</li><li><strong>类型：</strong><ul><li>Dromara 社区内的现有项目。</li><li>孵化 PMC。</li></ul></li><li><strong>职责：</strong><ul><li>提供候选项目的初始审批。</li><li>提名导师并协助孵化。</li></ul></li><li><strong>其他：</strong><ul><li>赞助方通过导师在孵化过程中正式代表其立场。</li></ul></li></ul><h2 id="导师-mentor" tabindex="-1">导师（Mentor） <a class="header-anchor" href="#导师-mentor" aria-label="Permalink to &quot;导师（Mentor）&quot;">​</a></h2><ul><li><strong>角色：</strong> 由赞助方指派，指导孵化项目并确保其遵循社区原则。</li><li><strong>职责：</strong><ul><li><strong>对孵化项目：</strong><ul><li>确保 PMC 决议及时执行；</li><li>协调孵化项目与社区间的沟通；</li><li>协助解决基础设施支持、授权问题等事务。</li></ul></li><li><strong>对 PMC：</strong><ul><li>监控孵化项目的进展；</li><li>评估项目是否符合孵化要求；</li><li>提交关于项目状态的报告。</li></ul></li><li><strong>对赞助方：</strong><ul><li>向赞助方汇报孵化项目的进展。</li></ul></li></ul></li></ul><h2 id="提交者-committer" tabindex="-1">提交者（Committer） <a class="header-anchor" href="#提交者-committer" aria-label="Permalink to &quot;提交者（Committer）&quot;">​</a></h2><ul><li><strong>角色：</strong> 负责代码提交及项目发展。</li><li><strong>要求：</strong><ul><li>保持与导师的沟通，确保孵化相关事务透明。</li><li>积极吸纳新的提交者。</li><li>确保决策过程公开可审计。</li><li>遵守 Dromara 社区的政策和流程。</li></ul></li><li><strong>目标：</strong> 展现社区协作精神，制定符合 Dromara 实践的贡献者引入文档。</li></ul><p>此描述体现了 Dromara 开源社区孵化流程中各角色的责任与协作方式，为新成员参与提供清晰指引。</p>',18)]))}const c=i(t,[["render",n]]);export{d as __pageData,c as default};
